query business ($limit: Int, $offset: Int, $id: String) {
    business (id: $id) {
        name
        id
        is_claimed
        is_closed
        url
        phone
        display_phone
        review_count
        categories {
            title
            alias
        }
        rating
        price
        location {
            address1
            address2
            address3
            city
            state
            zip_code
            country
            formatted_address
        }
        coordinates {
            latitude
            longitude
        }
        photos
        hours {
            hours_type
            open {
                is_overnight
                end
                start
                day
            }
            is_open_now
        }
        reviews (limit: $limit, offset: $offset) {
            rating
            user {
                image_url
                name
            }
            text
            time_created
            url
        }
        attributes
    }
}

